import React, { useState } from "react";
import {
  FlatList,
  StyleSheet,
  Text,
  View,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import { Card } from "react-native-elements";
import { REWARDS } from "../shared/rewards";
import { useSelector, useDispatch } from "react-redux";
import { addPoints, addRewardsQ } from "../redux/ActionCreators";

export default function RewardsPendingRO() {
  const rewards = useSelector((state) => state.rewards);
  const rewardsQ = useSelector((state) => state.rewardsQueue);
  const dispatch = useDispatch();

  return (
    <View style={styles.container}>
      <FlatList
        data={rewardsQ.filter((item) => item.fulfilled === null)}
        extraData={rewardsQ}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <View style={styles.itemText}>
              <Text>{item.name}</Text>
            </View>
            <View style={styles.itemPoints}>
              <Text
                style={{
                  textAlign: "right",
                  color: item.points < 0 ? "red" : "green",
                }}
              >
                {item.points}
              </Text>
            </View>
          </View>
        )}
        keyExtractor={(item) => item.id.toString()}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    margin: 22,
  },
  item: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: "#ffffff",
    padding: 10,
    margin: 5,
    fontSize: 20,
    borderRadius: 5,
    alignItems: "center",
  },
  itemText: {
    flex: 1,
    flexBasis: 140,
    flexGrow: 1,
  },
  itemPoints: {
    flex: 1,
    flexBasis: 30,
    flexShrink: 1,
  },
  itemButton: {
    flex: 1,
    flexBasis: 25,
    padding: 10,
    alignSelf: "flex-end",
    margin: 1,
    backgroundColor: "white",
    borderColor: "black",
    borderWidth: 1,
  },
  header: {
    backgroundColor: "#F5FCFF",
    padding: 10,
  },
  content: {
    padding: 20,
    backgroundColor: "#fff",
  },
  headerText: {
    textAlign: "center",
    fontSize: 16,
    fontWeight: "500",
  },
});
