import React, { useState } from "react";
import { FlatList, StyleSheet, Text, View, ScrollView, TouchableOpacity } from "react-native";
import { Card, Icon, Button } from "react-native-elements";
import { BEHAVIORS } from "../shared/behaviors";
import { useSelector, useDispatch } from "react-redux";
import { addPoints, approveBehaviorQ, deleteBehaviorQ } from "../redux/ActionCreators"

export default function BehaviorPending() {
  const behaviors = useSelector((state) => state.behaviors);
  const behaviorQ = useSelector((state) => state.behaviorQueue);
  const dispatch = useDispatch();

  return (
    <View style={styles.container}>
    <FlatList
      data={behaviorQ.filter((item) => item.approval === null)}
      extraData={behaviorQ}
      renderItem={({ item }) => (
        <View style={styles.item}>
          <View style={styles.itemText}>
            <Text>{item.name}</Text>
          </View>
          <View style={styles.itemPoints}>
            <Text
              style={{
                textAlign: "center",
                color: item.points < 0 ? "red" : "green",
              }}
            >
              ({item.points})
            </Text>
          </View>
          <Button
            icon={
              <Icon
                name="check"
                type="font-awesome"
                color="green"
                size={14}
              />
            }
            buttonStyle={styles.itemButton}
            onPress={() => {
              dispatch(approveBehaviorQ(item.id));
              dispatch(addPoints(item.points));
            }}
          />
          <Button
            icon={
              <Icon
                name="times"
                type="font-awesome"
                color="red"
                size={14}
              />
            }
            buttonStyle={styles.itemButton}
            onPress={() => dispatch(deleteBehaviorQ(item.id))}
          />
        </View>
      )}
      keyExtractor={(item) => item.id.toString()}
    />
  </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      margin: 22,
    },
    item: {
      flex: 1,
      flexDirection: "row",
      backgroundColor: "#ffffff",
      padding: 10,
      margin: 5,
      fontSize: 20,
      borderRadius: 5,
      alignItems: "center",
    },
    itemText: {
      flex: 1,
      flexBasis: 140,
      flexGrow: 1,
    },
    itemPoints: {
      flex: 1,
      flexBasis: 30,
      flexShrink: 1,
    },
    itemButton: {
      flex: 1,
      flexBasis: 25,
      padding: 10,
      alignSelf: "flex-end",
      margin: 1,
      backgroundColor: "white",
      borderColor: "black",
      borderWidth: 1,
    },
    header: {
      backgroundColor: "#F5FCFF",
      padding: 10,
    },
    content: {
      padding: 20,
      backgroundColor: "#fff",
    },
    headerText: {
      textAlign: "center",
      fontSize: 16,
      fontWeight: "500",
    },
});
